css:
/* sesuaikan page dengan size barcode */
/* page only working for non pdf print */
@page {
  size: 50mm 22mm;
  margin: 0!important;
  padding: 0!important;
}

body {
  width: 100%;
  margin: 0;
  padding: 0;
  font-family: Arial, sans-serif;
}

.cont {
  width: 50mm;
  height: 20mm;
}

.cont img {
    height: 20mm;
}

.print-format {
    padding: 0!important;
    margin:0!important;
}

